import React, { useState } from 'react';
import Navbar from '../components/Navbar';

const About = () => {
    const [expenses, setExpenses] = useState([]);
    const [incomes, setIncomes] = useState([]);

    const addExpense = () => {
        const newExpense = { id: Date.now(), name: 'New Expense', amount: 100 };
        setExpenses([...expenses, newExpense]);
    };

    const deleteExpense = (expenseId) => {
        setExpenses(expenses.filter(expense => expense.id !== expenseId));
    };

    const addIncome = () => {
        const newIncome = { id: Date.now(), name: 'New Income', amount: 100 };
        setIncomes([...incomes, newIncome]);
    };

    const deleteIncome = (incomeId) => {
        setIncomes(incomes.filter(income => income.id !== incomeId));
    };

    return (
        <div>
            <Navbar />
            <h1 className="text-3xl font-bold underline mb-4">My Finances</h1>
            <div className="flex flex-wrap -m-2">
                {/* Existing UI Elements */}
                {/* ... */}

                {/* Add Expense and Income Buttons */}
                <button onClick={addExpense} className="bg-green-500 text-white p-2 rounded m-2">
                    Add Expense
                </button>
                <button onClick={addIncome} className="bg-blue-500 text-white p-2 rounded m-2">
                    Add Income
                </button>

                {/* Display Expenses */}
                {expenses.map(expense => (
                    <div key={expense.id} className="bg-red-100 p-6 rounded-lg shadow-md m-2">
                        {expense.name}: ${expense.amount}
                        <button onClick={() => deleteExpense(expense.id)}
                                className="bg-red-500 text-white p-2 rounded ml-2">
                            Delete
                        </button>
                    </div>
                ))}

                {/* Display Incomes */}
                {incomes.map(income => (
                    <div key={income.id} className="bg-green-100 p-6 rounded-lg shadow-md m-2">
                        {income.name}: ${income.amount}
                        <button onClick={() => deleteIncome(income.id)}
                                className="bg-red-500 text-white p-2 rounded ml-2">
                            Delete
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default About;
