import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import firebase from '../firebase'; // Ensure firebase is correctly configured and imported
import { auth } from './firebase';

const Home = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isUserLoggedIn, setIsUserLoggedIn] = useState(false);

    // Check if user is logged in
    firebase.auth().onAuthStateChanged(user => {
        if (user) {
            setIsUserLoggedIn(true);
        } else {
            setIsUserLoggedIn(false);
        }
    });

    const handleSignUp = async () => {
        try {
            await firebase.auth().createUserWithEmailAndPassword(email, password);
            console.log('Sign up successful');
        } catch (error) {
            console.error('Error signing up:', error);
        }
    };

    const handleLogin = async () => {
        try {
            await firebase.auth().signInWithEmailAndPassword(email, password);
            console.log('Login successful');
        } catch (error) {
            console.error('Error logging in:', error);
        }
    };

    const handleLogout = async () => {
        try {
            await firebase.auth().signOut();
            console.log('Logout successful');
        } catch (error) {
            console.error('Error logging out:', error);
        }
    };

    return (
        <div>
            <Navbar />
            <div className="container mx-auto p-4">
                {!isUserLoggedIn ? (
                    <div>
                        <h1 className="text-3xl font-bold mb-4">Welcome to FinApp</h1>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Email"
                            className="p-2 border rounded mb-2"
                        />
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Password"
                            className="p-2 border rounded mb-2"
                        />
                        <button onClick={handleSignUp} className="bg-blue-500 text-white p-2 rounded mr-2">Sign Up</button>
                        <button onClick={handleLogin} className="bg-green-500 text-white p-2 rounded">Login</button>
                    </div>
                ) : (
                    <div>
                        <h1 className="text-3xl font-bold mb-4">You are logged in!</h1>
                        <button onClick={handleLogout} className="bg-red-500 text-white p-2 rounded">Logout</button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Home;
