import React, { useState } from 'react';
import Navbar from '../components/Navbar';

const About = () => {
    const [expenses, setExpenses] = useState([]);
    const [incomes, setIncomes] = useState([]);
    const [newExpenseAmount, setNewExpenseAmount] = useState('');
    const [newIncomeAmount, setNewIncomeAmount] = useState('');

    const addExpense = () => {
        if (!newExpenseAmount) return;
        const newExpense = { id: Date.now(), name: 'Expense', amount: parseFloat(newExpenseAmount) };
        setExpenses([...expenses, newExpense]);
        setNewExpenseAmount('');
    };

    const deleteExpense = (expenseId) => {
        setExpenses(expenses.filter(expense => expense.id !== expenseId));
    };

    const addIncome = () => {
        if (!newIncomeAmount) return;
        const newIncome = { id: Date.now(), name: 'Income', amount: parseFloat(newIncomeAmount) };
        setIncomes([...incomes, newIncome]);
        setNewIncomeAmount('');
    };

    const deleteIncome = (incomeId) => {
        setIncomes(incomes.filter(income => income.id !== incomeId));
    };

    return (
        <div>
            <Navbar />
            <h1 className="text-3xl font-bold underline mb-4">My Finances</h1>
            <div className="flex flex-wrap -m-2">
                {/* Expenses Section */}
                <div className="w-full md:w-1/2 p-2">
                    <div className="bg-blue-100 p-6 rounded-lg shadow-md">
                        <h2 className="text-xl font-bold mb-3">Expenses</h2>
                        <input
                            type="number"
                            value={newExpenseAmount}
                            onChange={(e) => setNewExpenseAmount(e.target.value)}
                            placeholder="Expense Amount"
                            className="p-2 border rounded mb-2"
                        />
                        <button onClick={addExpense} className="bg-red-500 text-white p-2 rounded">
                            Add Expense
                        </button>
                        <div className="mt-4">
                            {expenses.map(expense => (
                                <div key={expense.id} className="bg-red-100 p-3 rounded-lg shadow-md mb-2">
                                    {expense.name}: ${expense.amount.toFixed(2)}
                                    <button onClick={() => deleteExpense(expense.id)}
                                            className="bg-red-500 text-white p-1 rounded ml-2">
                                        Delete
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

                {/* Incomes Section */}
                <div className="w-full md:w-1/2 p-2">
                    <div className="bg-green-100 p-6 rounded-lg shadow-md">
                        <h2 className="text-xl font-bold mb-3">Incomes</h2>
                        <input
                            type="number"
                            value={newIncomeAmount}
                            onChange={(e) => setNewIncomeAmount(e.target.value)}
                            placeholder="Income Amount"
                            className="p-2 border rounded mb-2"
                        />
                        <button onClick={addIncome} className="bg-blue-500 text-white p-2 rounded">
                            Add Income
                        </button>
                        <div className="mt-4">
                            {incomes.map(income => (
                                <div key={income.id} className="bg-green-100 p-3 rounded-lg shadow-md mb-2">
                                    {income.name}: ${income.amount.toFixed(2)}
                                    <button onClick={() => deleteIncome(income.id)}
                                            className="bg-red-500 text-white p-1 rounded ml-2">
                                        Delete
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default About;
